  useEffect(() => {
    window.navigator.geolocation
      ? window.navigator.geolocation.getCurrentPosition(
          successPosition,
          failurePosition
        )
      : alert(
          "Your browser doesn't support geolocation! Location set to Leiria"
        );
  }, []);

  const [coords, setCoords] = useState({
    latitude: 39.74362,
    longitude: -8.80705,
  });

