import React from "react";

const ForecastWeatherCard = ({ data }) => {
  const handleClick = (e) => {};
  return (
    <div className="forecast-card" onClick={(e) => handleClick(e)}>
      <div></div>
      <div className="forecast-icon-text">
        <div className="forecast-icon"></div>
        <div className="forecast-text">{data.weather[0].description.charAt(0).toUpperCase() +
              data.weather[0].description.slice(1)}</div>
      </div>
      <div className="forecast-max-min">
        <div className="forecast-temp">
          <span>Máx.</span> 
          <span>{Math.round(data.temp?.max - 273.15)}º</span>
        </div>
        <div className="forecast-temp">
          <span>Min.</span> 
          <span>{Math.round(data.temp?.min - 273.15)}º</span>
        </div>
      </div>
    </div>
  );
};

export default ForecastWeatherCard;
