import React from "react";
import sunny from "../icons/sunny.png";
import "../styles/style.css";

const CurrentWeather = ({data}) => {
   

setInterval(()=>{

}, 1000);
const getTimeAndDate =() => {
    const date = new Date()
const day = date.toString().split(" ")[2]
const month = date.toString().split(" ")[1]
const year = date.getUTCFullYear().toString().slice(2,4)

const timeAndDate = `${time} - ${weekday}, ${day} ${month} '${year}`

return timeAndDate
}


const time =  date.toTimeString().slice(0,5)+'H'
const weekday = date.toLocaleString('en-us', {weekday:'long'}).slice(0,3);
  return (
    <div className="current-weather-wrapper">
      <div>
        <h1 className="current-temperature">{Math.round(data?.main?.temp-273.15)}ยบ</h1>
      </div>
      <div className="current-location-time">
        <h3 className="current-location">{data?.name}</h3>
        <h5 className="current-time"></h5>
      </div>
      <div className="current-weather-icon-text">
        <img className="current-weather-icon" src={sunny} alt="weather-icon" />
        <h6 className="current-weather-text">{data&&data.weather?(data.weather[0].description.charAt(0).toUpperCase() + data.weather[0].description.slice(1) ):""}</h6>
      </div>
    </div>
  );
};

export default CurrentWeather;
