    setCoords({ latitude: coords.latitude, longitude: coords.longitude });
  };

  const failurePosition = ({ message }) => {
    alert(message + ". Location set to Leiria!");
    setCoords({ latitude: 39.74362, longitude: -8.80705 });
  };

  

  return <div>APP</div>;
};

export default App;
