import React from "react";

const ForecastWeatherCard = ({ data, day }) => {
    
    const getTimeAndDate = (dayDiference) => {
        const date = new Date();
        const day = date.toString().split(" ")[2];
        const month = date.toString().split(" ")[1];
        const year = date.getUTCFullYear().toString().slice(2, 4);
        const time = date.toTimeString().slice(0, 5) + "H";
        const weekday = date
          .toLocaleString("en-us", { weekday: "long" })
          .slice(0, 3);
        return `${time} - ${weekday}, ${day} ${month} '${year}`;
    };

  const handleClick = (e) => {};
  return (
    <div className="forecast-card" onClick={(e) => handleClick(e)}>
      <div></div>
      <div className="forecast-icon-text">
        <div className="forecast-icon"></div>
        <div className="forecast-text">{data.weather[0].main}</div>
      </div>
      <div className="forecast-max-min">
        <div className="forecast-temp">
          <span>Máx.</span>
          <span>{Math.round(data.temp?.max - 273.15)}º</span>
        </div>
        <div className="forecast-temp">
          <span>Min.</span>
          <span>{Math.round(data.temp?.min - 273.15)}º</span>
        </div>
      </div>
    </div>
  );
};

export default ForecastWeatherCard;
