import React, { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const API_KEY = "5e5765a835fd5c6a2e13ed922a1a6f46";
  const [coords, setCoords] = useState({});

  const [currentData, setCurrentData] = useState({});
  const [dailyData, setDailyData] = useState({});

  console.log(currentData)
  console.log(dailyData)
  console.log("im rendering")
  const [exclude, setExcluse] = useState("");

  useEffect(() => {
    if (window.navigator.geolocation) {
      window.navigator.geolocation.getCurrentPosition(
        successPosition,
        failurePosition
      );
    } else {
      alert("Your browser doesn't support geolocation! Location set to Leiria");
      setCoords({ latitude: 39.74362, longitude: -8.80705 });
    }
  }, []);

  useEffect(() => {
    if (Object.keys(coords).length !== 0) {
      axios
        .get(
          `https://api.openweathermap.org/data/2.5/onecall?lat=${coords.latitude}&lon=${coords.longitude}&exclude=${exclude}&appid=${API_KEY}`
        )
        .then(({ data }) => {
          setCurrentData(data.current)
          setDailyData(data.daily)
        });
    }
  }, [coords]);

  const successPosition = ({ coords }) => {
    setCoords({ latitude: coords.latitude, longitude: coords.longitude });
  };

  const failurePosition = ({ message }) => {
    alert(message + ". Location set to Leiria!");
    setCoords({ latitude: 39.74362, longitude: -8.80705 });
  };

  return <div>APP</div>;
};

export default App;
