import React, { useEffect } from "react";

const ForecastWeatherOpened = ({ setOpenDetailCard, data }) => {
  useEffect(() => {
    setInterval(() => {
      document
        .getElementsByClassName("forecast-opened-wrapper")[0]
        .classList.remove("fade-out");

      document
        .getElementsByClassName("forecast-opened-wrapper")[0]
        .add("fade-in");
    }, 1000);
  }, []);

  return (
    <div className="forecast-opened-wrapper fade-out">
      <div className="forecast-opened">
        <div className="forecast-title"></div>
        <div className="forecast-body">
          <div className="forecast-info">
            <span>Current temperature: 23ยบ</span>
            <span>Feels like: 26ยบ</span>
            <span>Wind speed: 3m/s</span>
            <span>Humidity: 30%</span>
          </div>
          <hr className="card-divider" />
          <div className="forecast-info">
            <span className="forecast-card-icon">
              <div
                className={`icon${data.weather[0].icon} forecast-icon`}
              ></div>
            </span>
            <span className="forecast-card-description">
              Weak wind and high temperatures make May a good starting month for
              enjoying the beach
            </span>
          </div>
        </div>
        <div>
          <i
            className="large times circle icon close-opened"
            onClick={() => {
              setOpenDetailCard(false);
            }}
          ></i>
        </div>
      </div>
    </div>
  );
};

export default ForecastWeatherOpened;
