import React, { useState, useEffect } from "react";
import ForecastWeatherCard from "./ForecastWeatherCard";
import ForecastWeatherOpened from "./ForecastWeatherOpened";

const ForecastWeather = ({ data }) => {
  const [openDetailCard, setOpenDetailCard] = useState(false);
  const [openDetailCard2, setOpenDetailCard2] = useState(false);
  console.log("LOG*****")

  

  useEffect(() => {
    if (openDetailCard) {
      fadein();
    }
  }, [openDetailCard]);

  return (
    <>
      {data.daily === undefined ? null : openDetailCard2 ? (
        <div className="forecast-opened-wrapper">
          <ForecastWeatherOpened
            setOpenDetailCard={setOpenDetailCard}
            data={openDetailCard2}
          />
        </div>
      ) : (
        <div className="forecast-wrapper">
          <button
            onClick={() => {
              fadein();
            }}
          >
            CLick me !
          </button>
          <ForecastWeatherCard
            data={data.daily[1]}
            offSet={1}
            setOpenDetailCard={setOpenDetailCard}
          />
          <ForecastWeatherCard
            data={data.daily[2]}
            offSet={2}
            setOpenDetailCard={setOpenDetailCard}
          />
          <ForecastWeatherCard
            data={data.daily[3]}
            offSet={3}
            setOpenDetailCard={setOpenDetailCard}
          />
          <ForecastWeatherCard
            data={data.daily[4]}
            offSet={4}
            setOpenDetailCard={setOpenDetailCard}
          />
          <ForecastWeatherCard
            data={data.daily[5]}
            offSet={5}
            setOpenDetailCard={setOpenDetailCard}
          />
          <ForecastWeatherCard
            data={data.daily[6]}
            offSet={6}
            setOpenDetailCard={setOpenDetailCard}
          />
          <ForecastWeatherCard
            data={data.daily[7]}
            offSet={7}
            setOpenDetailCard={setOpenDetailCard}
          />
        </div>
      )}
    </>
  );
};

export default ForecastWeather;
